mapper.select:
select from schedule_job
and bean_name like concat('%',#{search},'%') or method_name like concat('%',#{search},'%') or remark like concat('%',#{search},'%')
order by job_id ${order}

mapper.delete:
delete from schedule_job where job_id
in

  #{jobId}

mapper.update:
update  schedule_job set status=#{status}
where job_id in

  #{jobId}
